import React, { useState } from 'react'
import "../Styles/adminlogin.css"
import { Link } from 'react-router-dom'
export default function AdminLogin() {
  let [username,setusername]=useState("");
  let [password,setpassword]=useState("")
  let verify=()=>{
    axios.get(`http://localhost:8080/api/admins/verify-by-email?email=${username}&password=${password}`)
  }
  return (
    <div className='adminpage'>
     <form action="" onSubmit={verify}>
            <label htmlFor="">UserName/Email</label>
            <input type="text" required placeholder='enter the username' 
                     value={username} onChange={(e)=>{setusername(e.target.value)}} />
            <label htmlFor="">Password</label>
            <input type="password" required placeholder='enter the password'
                    value={password} onChange={(e)=>{setpassword(e.target.value)}} />
            <button>LOGIN</button>
            <p>Are you a new user?<Link to="/adminsignup">Register here</Link></p>
        </form> 
    </div>
  )
}
